function processLoanRequest(){$(".approveLoan").click(function(e){e.preventDefault(),$.ajax({url:$(this).prop("href"),type:"get",success:function(e){new Noty({theme:"relax",text:"Loan Approved",type:"success",layout:"topRight",timeout:1500}).show(),$(".loan-"+e.data.loan._id).remove()}}).fail(function(){new Noty({theme:"relax",text:"Error",type:"error",layout:"topRight",timeout:1500}).show()})}),$(".rejectLoan").click(function(e){e.preventDefault(),$.ajax({url:$(this).prop("href"),type:"get",success:function(e){new Noty({theme:"relax",text:"Loan Request Rejected",type:"success",layout:"topRight",timeout:1500}).show(),$(".loan-"+e.data.loan._id).remove()}}).fail(function(){new Noty({theme:"relax",text:"Error",type:"error",layout:"topRight",timeout:1500}).show()})})}processLoanRequest();