function deleteNotification(){$(".deleteNotification").click(function(t){t.preventDefault(),$.ajax({url:$(this).prop("href"),type:"get",success:function(t){new Noty({theme:"relax",text:"Notification Deleted",type:"success",layout:"topRight",timeout:1500}).show(),$(".notification-"+t.data.notification._id).remove()}}).fail(function(){new Noty({theme:"relax",text:"Error",type:"error",layout:"topRight",timeout:1500}).show()})})}deleteNotification();